script:
function startVue() {

	var ItemsVue = new Vue({
		el: '#Itemlist',
		data: {
			items: []
		},
		mounted: function () {
			var self = this;
			$.ajax({
				url: 'default.asp?asplEvent=sampleform28_data',
				method: 'GET',
				success: function (data) {
					self.items = data.aspl;
				},
				error: function (error) {
					console.log(error);
				}
			});
		}
	});
}
