aspAjaxUrl="default.asp"

$('.aspAjax').submit(function(e) {
	e.preventDefault();
	aspAjax('POST',aspAjaxUrl,$(this).serialize(),aspAjaxSuccess);
});

function aspAjaxSuccess(data) {
	$('#hello').html(data);		
};	



