script:
$("#pdf").on("click", function () {

	// Default export is a4 paper, portrait, using milimeters for units
	var doc = new jsPDF()

	doc.setFontSize(16);

	doc.text('Hello world!', 20, 20)

	doc.setFontSize(12);

	doc.text('You don\'t need an expensive 3rd party component to create PDF files....', 20, 30)

	doc.save('a4.pdf')	
});
