script:
var i=0;
var j=0;
var files;

$("#uploadForm").submit(function(e){

	e.preventDefault()

	$("#uploadFBS").html('Upload started

')
	$("#uploadFB").html('')

	$("#loading").css("display","block");
	$("#alert").css("display","block");

	$("#uploadForm").find("input[type=file]").each(function(index, field){
		files=field.files		
		})	

	jQueryUpload()

});

function jQueryUpload() {	

	var file = files[i]
	var fd = new FormData(); 			
	fd.append('file', file)	

	$.ajax({		
		type: "post",
		url: "demo.asp?e=uploadfilejquery",
		dataType: "text",
		data: fd,
		contentType: false, 
		processData: false, 
		success: function (data) {			
					$("#uploadFB").append('' + file.name + ' was uploaded
')	
					i++					
					if(i<files.length) {jQueryUpload()}
					else {
					//done	
					finish()
					}
				},
		error: function (data) {
			$("#uploadFB").append('' + file.name + ' (' + Math.round(file.size/1024/1024) +' MB) was NOT uploaded
')	
			i++
			j++
			console.log(data)
			if(i<files.length) {jQueryUpload()}
			else {
				//done
				finish()
				}			
			}
		});
	}

function finish() {	
	$("#loading").css("display","none")	
	$("#uploadFBS").html('Upload finished

')	
	if(!j==0) {$("#uploadFB").prepend('' + j + ' files have NOT been uploaded (probably too big)

')}	
	if(!(i-j)==0) {$("#uploadFB").prepend('' + (i-j) + ' files have been uploaded

')}	
	i=0;j=0;
}
